<style>
    /* Orbit Section */
    .orbit-section {
        position: relative;
        width: 100%;
        max-width: 700px;
        height: 670px;
        margin: 0px auto;
        --orbit-radius: -250px;
    }

    .center {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        background: rgba(133, 91, 255, 0.2);
        width: 200px;
        height: 200px;
        border-radius: 50%;
